import path from 'path';
import { findMonorepoRoot, getMonorepoPackages, isModernjsMonorepo } from '@modern-js/utils';
import { memoize } from "./memoize";

const shouldIncludePackage = (p, include) => include.some(i => i instanceof RegExp ? i.test(p.name) : p.name === i);

export const getSourceIncludes = memoize((appDirectory, config) => {
  const {
    source
  } = config;
  const include = (source === null || source === void 0 ? void 0 : source.include) || [];
  const root = findMonorepoRoot(appDirectory);

  if (!root) {
    return include;
  }

  const packages = getMonorepoPackages(root);
  const modernjsMonorepo = isModernjsMonorepo(root);
  const paths = packages.filter(p => modernjsMonorepo && p.path.startsWith(path.join(root, 'features')) || shouldIncludePackage(p, include)).map(p => p.path);
  return [...paths, ...include];
});